def getTurbLoc(turb_loc_file_name):
	f = open(turb_loc_file_name,'r').readlines()
	l = []
	for line in f[1:]:
		line = line.strip().split(',')
		l.append([])
		l[-1].extend([float(line[0]),float(line[1])])
	turb_coords = np.array(l,dtype = np.float32)
	return(turb_coords)

