	rows = 40
	for i in range(1,rows*rows+1):
		if(lis[i-1]=="1"):
			x = (i - rows * ((i-1)//rows))*(4000/rows) - (4000/rows)/2
			y = ((i-1)//rows)*(4000/rows) + (4000/rows)/2
			# x = (i - rows * ((i-1)//rows))*(4000/rows) + np.random.normal(loc=200,scale=25)
